import React from 'react'
import { BsArrowLeftCircle, BsArrowRightCircle } from "react-icons/bs";
import { useState } from 'react';

import {data} from "../assets/data"
export const CarouselAsos = () => {
    
    const [current, setCurrent] = useState(0);
    const prev = () => {
        if (current === 0) {
          setCurrent(data.length - 1);
        } else {
          setCurrent(current - 1);
        }
    }

    const next = () => {
        if (current === data.length - 1) {
          setCurrent(0);
        } else {
          setCurrent(current + 1);
        }
    }

    
  return (
    <div className='overflow-hidden w-[1000px]  h-[800px] m-auto relative'>
        <div className='flex transition-transform  ease-in-out duration-500' 
        style={{ transform: `translateX(-${current * 100}%)`}}>
            {data.map((s)=>{
                return (
                    <img src={s.imgUrl} alt="" />
                )
            })}

        </div>
        <div className='absolute inset-0 flex justify-between items-center p-4'>
            <button onClick={prev} className='p-1 rounded-full shadow bg-white hover:bg-gray-200'>
                <BsArrowLeftCircle/>
            </button>
            <button onClick={next} className='p-1 rounded-full shadow bg-white hover:bg-gray-200'>
                <BsArrowRightCircle/>
            </button>
        </div>

    </div>
  )
};
